// Set up the provider and signer
const provider = new ethers.providers.JsonRpcProvider(
  "https://alpha-rpc.scroll.io/l2"
);
const privateKey =
  "f2846882e7e17daefeda2f835693c1f0a29aa0cd277cd59c2b431ada5eaa3807"; // Replace with your private key
const wallet = new ethers.Wallet(privateKey, provider);

// Create the contract instance
const contract = new ethers.Contract(contractAddress, contractABI, wallet);

// Function to mint an NFT
async function mintNFT(toAddress, tokenURI) {
  try {
    // Call the mint function in your smart contract
    const transaction = await contract.mint(toAddress, tokenURI);

    // Wait for the transaction to be mined
    const receipt = await transaction.wait();

    // Get the token ID from the event emitted by the contract
    const tokenId = receipt.events[0].args.tokenId.toNumber();

    console.log("NFT minted successfully!");
    console.log("Token ID:", tokenId);
  } catch (error) {
    console.error("Error minting NFT:", error);
  }
}

